import React, { useState } from 'react'
import { Button } from 'react-bootstrap'

const SignIn = () => {
    const [user, setUser] = useState({
        firstname: "",
        lastname: "",
        email: "",
        password: ""
    })

    const [confirmPasswod, setConfirmPassword] = useState("")

    const handleChange = (event) => {
        const { name, value } = event.target;

        if(name === "confirmPasswod") {
            setConfirmPassword(value);
        }
        else {
            setUser((prev) => ({
                ...prev,
                [name] : value
            }))
        }
    }

    const handleSubmit = (event) => {
        event.preventDefault();

        console.log(user, confirmPasswod);
    }

  return (
    <div className='signup-page'>
        <h1>Sign Up</h1>
        <form onSubmit={handleSubmit} className='signin-form'>
            <input name='firstname' onChange={handleChange} value={user.firstname} required type='text' placeholder='Enter your First Name' />
            <input name='lastname' onChange={handleChange} value={user.lastname} type='text' placeholder='Enter your Last Name' />
            <input name='email' onChange={handleChange} value={user.email} required type='email' placeholder='Enter your Email' />
            <input name='password' onChange={handleChange} value={user.password} required type='password' placeholder='Enter your Password' />
            <input name='confirmPassword' onChange={handleChange} value={confirmPasswod} required type='password' placeholder='Confirm your Password' />
            <Button>Submit</Button>
        </form>
    </div>
  )
}

export default SignIn
