import axios from "axios";
import React, { useEffect, useState } from "react";
import Modal from 'react-modal';

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
};

export default function App() {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {

    async function fetchEmployees() {
      try {
        const res = await axios.get("http://localhost:8080/api/employee")
        setEmployees(res.data)
      } catch (error) {
        console.log(error)
      }
    }

    fetchEmployees();
  }, [])

  async function deleteEmployee(index) {
    try {
      const res = await axios.delete(`http://localhost:8080/api/employee/${index}`)
      if(res.status === 200) {
        alert("Employee successfully deleted");
        setEmployees(prevEmployees => prevEmployees.filter(employee => employee.id !== index));
      }
    } catch (error) {
      console.log(error)
    }
  }

  function renderEmployees() {
    return employees.map((employee, index) => (
      <tr key={index} style={index%2 === 0 ? {} : {backgroundColor: "#D3D3D3"} }>
        <td>{employee.firstname}</td>
        <td>{employee.lastname}</td>
        <td>{employee.email}</td>
        <td>{employee.role}</td>
        <td>{employee.dob}</td>
        <td>{employee.age}</td>
        <td>
          <button className="table-btn update-btn"><p>Update</p></button>
          <button onClick={() => deleteEmployee(employee.id)} className="table-btn delete-btn"><p>Delete</p></button>
        </td>
      </tr>
    ))
  }

  return (
    <div>
      <div className="header">
        <h1>Employee Management App</h1>
      </div>

      <div className="main">
        <h1>Employees List:</h1>
        <button className="add-btn"><h3>Add Employee</h3></button>

        <div>
          <table>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>DOB</th>
              <th>Age</th>
              <th>Actions</th>
            </tr>
            {
              employees.length > 0 && renderEmployees()
            }

          </table>

        </div>

      </div>

    </div>
  )
}
