import axios from "axios";
import React, { useEffect, useState } from "react";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

export default function App() {
  const [employees, setEmployees] = useState([]);

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {

    async function fetchEmployees() {
      try {
        const res = await axios.get("http://localhost:8080/api/employee")
        setEmployees(res.data)
      } catch (error) {
        console.log(error)
      }
    }

    fetchEmployees();
  }, [])

  async function deleteEmployee(index) {
    const confirmation = window.confirm(`Are you sure you want to delete the employee?`)
    if(confirmation === true) {
      try {
        const res = await axios.delete(`http://localhost:8080/api/employee/${index}`)
        if (res.status === 200) {
          alert("Employee successfully deleted");
          setEmployees(prevEmployees => prevEmployees.filter(employee => employee.id !== index));
        }
      } catch (error) {
        console.log(error)
      }
    } 
  }

  function renderEmployees() {
    return employees.map((employee, index) => (
      <tr key={index} style={index % 2 === 0 ? {} : { backgroundColor: "#D3D3D3" }}>
        <td>{employee.firstname}</td>
        <td>{employee.lastname}</td>
        <td>{employee.email}</td>
        <td>{employee.role}</td>
        <td>{employee.dob}</td>
        <td>{employee.age}</td>
        <td>
          <Button variant="warning" className="table-btn "><p>Update</p></Button>
          <Button variant="danger" onClick={() => deleteEmployee(employee.id)} className="table-btn"><p>Delete</p></Button>
        </td>
      </tr>
    ))
  }

  return (
    <div>
      <div className="header">
        <h1>Employee Management App</h1>
      </div>

      <div className="main">
        <h1>Employees List:</h1>
        <Button variant="primary" onClick={handleShow}>
        Add Employee
      </Button>

        <div>
          <table>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>DOB</th>
              <th>Age</th>
              <th>Actions</th>
            </tr>
            {
              employees.length > 0 && renderEmployees()
            }

          </table>

        </div>

      </div>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add an Employee</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form>
            <input type="text" placeholder="Enter the first name" />
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>

    </div>
  )
}
